import React from 'react';
import CountrySelect from './CountrySelect';
import Map from './Map';
import coordData from '../../resources/country-coord.json'

class Landing extends React.Component {

    constructor(props) {
        super(props);
        this.handleNext = this.handleNext.bind(this);
      }

      handleNext(event) {

        event.preventDefault();

        document.getElementById("userInfo").style.visibility = "hidden";
        var farmInfo = document.getElementById("farmInfo");
        farmInfo.classList.remove("hidden");
        var farmMap = document.getElementById("mapContainer");
        farmMap.classList.remove("hidden");

        //this.props.setStateOfParent("dashboardScreen");
      }

      toLogin(){

      }

    render() {
        return (

            <div id='landingContainer' className='screen'>

            <main id='landingCard'>

                <img src={require('../../resources/img/logo.png')} alt="agrosense logo"/>

                <form id="userInfo" onSubmit={this.handleNext}> 

                    <h2>Create your account</h2>
                    <label htmlFor="Name">Name</label><br/>
                    <input required type="text" id="Name" name="Name"/><br/>
                    <label htmlFor="email">Email</label><br/>
                    <input required type="email" id="email" name="email"/><br/>
                    <label htmlFor="pwd">Password</label><br/>
                    <input required type="password" id="pwd" name="pwd"/><br/>
                    <input type="submit" value="Next" />
                    <p>Already have an account?<span onClick={this.toLogin}>Login</span></p>

                </form>

                <form id="farmInfo" className="hidden" onSubmit={this.handleSubmit}> 

                    <h2>Details about your farm</h2>
                    <label htmlFor="farmName">Farm Name</label><br/>
                    <input required type="text" id="farmName" name="farmName"/><br/>
                    <label htmlFor="country">Country</label><br/>
                    <CountrySelect></CountrySelect>
                    <input type="submit" value="Next" />
                    <p>Already have an account?<span onClick={this.toLogin}>Login</span></p>

                </form>

            </main>

            <aside>
                <div className="hidden" id='mapContainer'>
                <Map></Map>
                </div>
            </aside>

            </div>

        );

    }

}

export default Landing;
