import React from 'react';
import CountrySelect from './CountrySelect';
import Map from './Map';
import coordData from '../../resources/country-coord.json'

class Landing extends React.Component {

    constructor(props) {

        super(props);
        this.handleNext = this.handleNext.bind(this);
        this.changeMap = this.changeMap.bind(this);
        this.handleMapClick = this.handleMapClick.bind(this)

        this.state = {
            country: {
            countryLat: 54, 
            countryLong: -2
            },
            marker: {
                markerLat: 0,
                markerLong: 0
            }
        };
      }

    handleNext(event) {

        event.preventDefault();

        document.getElementById("userInfo").style.visibility = "hidden";
        var farmInfo = document.getElementById("farmInfo");
        farmInfo.classList.remove("hidden");

        //this.props.setStateOfParent("dashboardScreen");
    }

    toLogin(){

    }

    changeMap() {

        var farmMap = document.getElementById("mapContainer");
        farmMap.classList.remove("hidden");

        var country = document.getElementById("country");
        var countryCoords = coordData.filter(function(coordData){return coordData.Country === country.value});

        this.setState({
            country: {
              countryLat: countryCoords[0].Latitude,
              countryLong: countryCoords[0].Longitude,
            }
          });

    }

    handleMapClick = (t, map, coord) => {

        const lat = t.latLng.lat();
        const lng = t.latLng.lng();

        this.setState({
            marker: { markerLat: lat, markerLong: lng }
          });

    }

    checkAccountComplete() {
        if(document.getElementById("name").value && document.getElementById("email").value && document.getElementById("pwd").value ){
            document.getElementById("accountNext").classList.add("submitActive");
        } else {
            document.getElementById("accountNext").classList.remove("submitActive");
        }
    }

    checkFarmComplete() {
        if(document.getElementById("name").value && document.getElementById("email").value && document.getElementById("pwd").value ){
            document.getElementById("accountNext").classList.add("submitActive");
        } else {
            document.getElementById("accountNext").classList.remove("submitActive");
        }
    }



    render() {

        return (

            <div id='landingContainer' className='screen'>

            <main id='landingCard'>

                <img src={require('../../resources/img/logo.png')} alt="agrosense logo"/>

                <form id="userInfo" onSubmit={this.handleNext}> 

                    <h2>Create your account</h2>

                    <label htmlFor="Name">Name</label><br/>
                    <input onChange={this.checkAccountComplete} required type="text" id="name" name="Name"/><br/>

                    <label htmlFor="email">Email</label><br/>
                    <input onChange={this.checkAccountComplete} required type="email" id="email" name="email"/><br/>

                    <label htmlFor="pwd">Password</label><br/>
                    <input onChange={this.checkAccountComplete} required type="password" id="pwd" name="pwd"/><br/>

                    <input id="accountNext" type="submit" value="Next" />

                    <p>Already have an account?<span onClick={this.toLogin}>Login</span></p>

                </form>

                <form id="farmInfo" className="hidden" onSubmit={this.handleSubmit}> 

                    <h2>Details about your farm</h2>

                    <label htmlFor="farmName">Farm Name</label><br/>
                    <input onChange={this.checkFarmComplete} required type="text" id="farmName" name="farmName"/><br/>

                    <label htmlFor="country">Country</label><br/>
                    <CountrySelect onChange={this.checkFarmComplete} changeMap={this.changeMap}></CountrySelect>

                    <input type="submit" value="Next" />

                    <p>Already have an account?<span onClick={this.toLogin}>Login</span></p>

                </form>

            </main>

            <aside>
                <div className="hidden" id='mapContainer'>
                <Map markerPosition={{lat: this.state.marker.markerLat, lng: this.state.marker.markerLong}} onClick={this.handleMapClick} center={{ lat: this.state.country.countryLat, lng: this.state.country.countryLong }}></Map>
                </div>
            </aside>

            </div>

        );

    }

}

export default Landing;
